  var k=0;
  var v_data=new Array();
  var tb=document.all("DGlist");
  //var sql="select SDSYSLISTITEM_FIELDNAME ZD from SDSYSLISTITEM where SDSYSLISTITEM_ID=''"+lb+"'' AND  SDSYSLISTITEM_FIELDNAME<>''''"
  var response=Genersoft.CRM.SD_ListMgr.GridMainClass.GetDataSetBySql(''GETLISTCOLUMN'',lb,''^'');
  if(response.error)
  {
    alert("获取列表字段信息失败"+response.error.Message);
    return 0;
  }
  var ds=response.value;
  var v_order=new Array();
  for(var i=0;i<ds.Tables[0].Rows.length;i++){
    v_order[i]={};
    v_order[i]["Field"]=ds.Tables[0].Rows[i][''ZD'']
    v_order[i]["Index"]=search_filed(ds.Tables[0].Rows[i][''ZD'']);
  }
  for (var i=2; i<tb.rows.length-1; i++){
    if (tb.rows[i].cells[0].children[0].checked){
      v_data[k]={};
      for(var j=0;j<v_order.length;j++){
        v_data[k][v_order[j].Field]=$(tb.rows[i].cells[v_order[j].Index]).text();
      }
      k++;
    }
  }
//alert("d")
  //验证必须参照同一个订单
//console.log(v_data)
  if(v_data.length<=0)
    v_data=null;

        G_callBackDialog(v_data); 
//console.log("11")
  //window.returnValue=v_data;
  //window.close();
}
